import React from "react";
import { Container } from "react-bootstrap";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { FiPhoneCall, FiMail, FiMap } from "react-icons/fi";

const Styles = styled.div`
  background-color: #4a536b;

  a {
    color: #fff;
    text-decoration: none;
    font-size: 14px;
  }

  .title,
  .sub-title {
    color: #000;
  }

  .footer-content {
    padding: 20px 0;
  }

  .footer-copyright {
    background: #000;
    color: #fff;
    padding: 20px 0;
  }
  span {
    padding: 0 5px;
  }

  svg {
    height: 40px;
    width: 40px;
  }
`;

const FooterPage = () => {
  return (
    <Styles>
      <div className="footer-content">
        <Container>
          <div className="row">
            <div className="col-12 col-md-3 col-lg-3 col-xl-3">

            </div>
            <div className="col-12 col-md-3 col-lg-3 col-xl-3">
            <div><FiPhoneCall/></div>
            <div className="mark1">Telefon Numarası</div>
            <div className="info">0850 000 00 00</div>
            </div>
            <div className="col-12 col-md-3 col-lg-3 col-xl-3">
            <div><FiMail/></div>
            <div className="mark2">E-mail</div>
            <div className="info">example@deniztaksi.com</div>
            </div>
            <div className="col-12 col-md-3 col-lg-3 col-xl-3">
            <div><FiMap/></div>
            <div className="mark3">Adres</div>
            <div className="info">Example Address</div>
            </div>
            {/* <div className="col-6">
              <ul>
                <li className="list-unstyled">
                  <a href="#!">Link 1</a>
                </li>
                <li className="list-unstyled">
                  <a href="#!">Link 2</a>
                </li>
                <li className="list-unstyled">
                  <a href="#!">Link 3</a>
                </li>
                <li className="list-unstyled">
                  <a href="#!">Link 4</a>
                </li>
              </ul>
            </div> */}
          </div>
        </Container>
      </div>
      <div className="footer-copyright py-3">
        <Container>
          <div className="row">
            <div class="col-6">
              &copy; {new Date().getFullYear()} Copyright{" "}
              <Link href="deniztaksi.com"> deniztaksi.com </Link>
            </div>
            <div class="col-6 text-right">
              <Link href="/">SSS</Link>
              <span>|</span>
              <Link href="/">Güvenlik Sözleşmesi</Link>
            </div>
          </div>
        </Container>
      </div>
    </Styles>
  );
};

export default FooterPage;
