import React from "react";

export default class TravelForm extends React.Component {
  state = {
    firstStation: "",
    lastStation: "",
    numberOfPassengers: "",
    date: "",
  };

  change = e => {
    this.props.onChange({ [e.target.name]: e.target.value });
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    // this.props.onSubmit(this.state);
    this.setState({
        firstStation: "",
        lastStation: "",
        numberOfPassengers: "",
        date: "",
    });
    this.props.onChange({
        firstStation: "",
        lastStation: "",
        numberOfPassengers: "",
        date: "",
    });
  };

  render() {
    return (
      <form>
        <input
          name="firstStation"
          placeholder="First Station"
          value={this.state.firstStation}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="lastStation"
          placeholder="Last Station"
          value={this.state.lastStation}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="numberOfPassengers"
          placeholder="numberOfPassengers"
          value={this.state.numberOfPassengers}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="date"
          placeholder="Date"
          value={this.state.date}
          onChange={e => this.change(e)}
        />
        <br />
        <button onClick={e => this.onSubmit(e)}>Rezervasyon Yap</button>
      </form>
    );
  }
}
