import React from "react";
import { Container, DropdownButton, Dropdown } from "react-bootstrap";
import styled from "styled-components";
import { BsArrowLeftRight } from "react-icons/bs";

const Styles = styled.div`
  height: 45px;

  form {
    border-radius: 20px;
  }

  .btn-group .btn-secondary {
    background-color: #fff;
    border-color: #fff;
    color: #132968;
    font-weight: bold;
  }

  .round-arrow {
    display: flex;
    justify-content: center;
  }

  .round-arrow button {
    border: none;
    border-radius: 50%;
    width: 40px;
    height: 40px;
    background-color: #f0f0f0;
    color: #4a536b;
    border: 2px solid #4a536b;
    position: absolute;
    z-index: 5;
    top: -20px;
  }

  .dropdown .btn-primary{
    color: red;
    background: none;
  }
  
`;

export const TravelForm = () => (
  <Styles>
    <Container>
      <div className="row">
        <div className="col-5"></div>
        <form className="col-7 shadow p-3 mb-5 bg-white rounded">
          <div className="row">
            <div className="col-6">
              <div className="btn-group">
                <div className="row">
                  <div className="col-6">
                  <DropdownButton id="dropdown-item-button" title="Dropdown button">
                    <Dropdown.ItemText>Dropdown item text</Dropdown.ItemText>
                    <Dropdown.Item as="button">Action</Dropdown.Item>
                    <Dropdown.Item as="button">Another action</Dropdown.Item>
                    <Dropdown.Item as="button">Something else</Dropdown.Item>
                  </DropdownButton>
                  </div>
                  <div className="col-6 dropdown">
                  <DropdownButton id="dropdown-item-button" title="Dropdown button">
                    <Dropdown.ItemText>Dropdown item text</Dropdown.ItemText>
                    <Dropdown.Item as="button">Action</Dropdown.Item>
                    <Dropdown.Item as="button">Another action</Dropdown.Item>
                    <Dropdown.Item as="button">Something else</Dropdown.Item>
                  </DropdownButton>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <input
                name="firstStation"
                placeholder="İlk Durak"
                className="form-control"
              />
            </div>
            <div className="col-12 round-arrow">
              <button>
                <BsArrowLeftRight />
              </button>
            </div>
            <div className="col-12">
              <input
                name="firstStation"
                placeholder="Son Durak"
                className="form-control"
              />
            </div>
            <div className="col-12"></div>
            <div className="col-4">
              <button type="submit" class="btn btn-primary mb-2 mt-3">
                Rezervasyon Yap
              </button>
            </div>
          </div>
        </form>
      </div>
    </Container>
  </Styles>
);
