import React from "react";
import styled from "styled-components";
import { Container } from "react-bootstrap";
import { BiMailSend } from "react-icons/bi";

const Styles = styled.div`
  .left {
    text-align: right;
  }

  .box .left svg {
    width: 40px;
    height: 40px;
    color: #aed6dc;
    text-align: center;
  }

`;
export const About = () => (
  <Styles>
    <div className="vertical-process">
      <Container>
          <h4 className="title">Hakkımızda</h4>
          <div className="row">
            <div className="col-6 box left">
              <div className="icon">
                <BiMailSend/>
              </div>
              <div className="description">
              'Rezervasyon Yap' seçeneğiyle sistemimiz üzerinden 7/24 veya rezervasyon@deniztaksi.com adresine mail göndererek rezarvasyon yaptırabilirsiniz.
              </div>
            </div>
            <div className="col-6 box right">
              
            </div>
          </div>
      </Container>
    </div>
  </Styles>
);
