import React from 'react';
import styled from "styled-components";
import { Container } from "react-bootstrap";


const Styles = styled.div`
  .middle {
    background-color: #fff;
    padding: 20px 0;
  }

  .middle-links a {
    background-color: #fff;
    border: 1px solid #ff9a8d;
    color: #ff9a8d;
    font-weight: bold;
    border-radius: 8px;
    text-align: center;
    padding: 10px 0;
    display: inherit;
  }

  .middle-links a:hover,
  .middle-links a:focus,
  .middle-links a:active {
    background-color: #f0f0f0;
    text-decoration: none;
  }

  .shadow-1:before {
    content: "";
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    width: inherit;
    height: inherit;
    z-index: -2;
    box-sizing: border-box;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.13);
  }
  
  .shadow-1:after {
    content: "";
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    width: inherit;
    height: inherit;
    z-index: -2;
    box-sizing: border-box;
    box-shadow: 0 2px 10px 0 rgba(0, 0, 0, 0.08);
  }
  
  .card {
    position: relative;
    height: 100px;
    background: #fcfcfc;
    margin: 20px 40px;
    transition: 0.4s all;
  }
  
  .card.open {
    height: 200px;
    background: #ffffff;
  }
  
  @media only screen and (min-width: 600px) {
    .card {
    width:500px;
      margin-top:20px;
      margin-bottom:20px;
      margin-left:auto;
      margin-right:auto;
    }
  }
  
  @media only screen and (max-device-width: 800px) and (orientation: portrait) {
    .card {
      margin: 12px 10px;
    }
  }
`;
export const About = () => (
  <Styles>
    <div className="vertical-process">
    <div className="title">Hakkımızda</div>
    <div class="cards">
      <div class="card shadow-1"></div>
      <div class="card shadow-1"></div>
      <div class="card shadow-1"></div>
      <div class="card shadow-1"></div>
      <div class="card shadow-1"></div>
    </div>
  </div>
  </Styles>
)
