import React from 'react';
import { Container } from 'react-bootstrap';
import styled from 'styled-components';

const Styles = styled.div`
  
`;

export const TravelForm = () => (
  <Styles>
    <form>
        <input
          name="firstStation"
          placeholder="First Station"
          value={this.state.firstStation}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="lastStation"
          placeholder="Last Station"
          value={this.state.lastStation}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="numberOfPassengers"
          placeholder="numberOfPassengers"
          value={this.state.numberOfPassengers}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="date"
          placeholder="Date"
          value={this.state.date}
          onChange={e => this.change(e)}
        />
        <br />
        <button onClick={e => this.onSubmit(e)}>Rezervasyon Yap</button>
      </form>
  </Styles>
)
