import React from "react";
import { Container } from 'react-bootstrap';
import styled from "styled-components";

const Styles = styled.div`
    height: 45px;
    form {
      position: relative;
      top: -90px;
      border-radius: 20px;
    }

    .btn-group .btn-secondary {
      background-color: #fff;
      border-color: #fff;
      color: #132968;
      font-weight: bold;
    }
`;

export const TravelForm = () => (
  <Styles>
    <Container>
      <form className="shadow p-3 mb-5 bg-white rounded">
        <div className="row">
          <div className="col-3">
          <div className="btn-group">
            <button className="btn btn-secondary btn-sm dropdown-toggle" type="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              Tek-Yön
            </button>
            <div className="dropdown-menu" aria-labelledby="dropdownMenu2">
              <button className="dropdown-item" type="button">Tek-Yön</button>
              <button className="dropdown-item" type="button">Çift-Yön</button>
            </div>
          </div>
          </div>
          <div className="col-3"></div>
        </div>
        <div className="row">
          <div className="col-3">
            <input
              name="firstStation"
              placeholder="First Station"
              className="form-control"
            />
          </div>
          <div className="col-3">
            <input
              name="firstStation"
              placeholder="First Station"
              className="form-control"
            />
          </div>
          <div className="col-4">

          </div>
          <div className="col-2">
            <button type="submit" class="btn btn-primary mb-2">Rezervasyon Yap</button>
          </div>
        </div>
      </form>
    </Container>
  </Styles>
);
