import React from "react";
import styled from "styled-components";
import { Container } from "react-bootstrap";

const Styles = styled.div`
height: 400px;
  .faq-content {
    margin: 0 auto;
  }

  .faq-question {
    padding: 20px 0;
    border-bottom: 1px dotted #ccc;
  }

  .panel-title {
    width: 100%;
    position: relative;
    margin: 0;
    padding: 0 10px 0 48px;
    display: block;
    cursor: pointer;
  }

  .panel-content {
    padding: 0px 14px;
    margin: 0 40px;
    height: 0;
    overflow: hidden;
    z-index: -1;
    position: relative;
    opacity: 0;
    -webkit-transition: 0.4s ease;
    -moz-transition: 0.4s ease;
    -o-transition: 0.4s ease;
    transition: 0.4s ease;
  }

  .panel:checked ~ .panel-content {
    height: auto;
    opacity: 1;
    padding: 14px 14px 0 14px;
  }

  .plus {
    position: absolute;
    z-index: 5;
    font-weight: bold;
    font-size: 20px;
    line-height: 100%;
    color: #ff9a8d;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -o-user-select: none;
    user-select: none;
    -webkit-transition: 0.2s ease;
    -moz-transition: 0.2s ease;
    -o-transition: 0.2s ease;
    transition: 0.2s ease;
  }

  .panel:checked ~ .plus {
    -webkit-transform: rotate(45deg);
    -moz-transform: rotate(45deg);
    -o-transform: rotate(45deg);
    transform: rotate(45deg);
    color: #aed6dc;
  }

  .panel {
    display: none;
  }
`;
export const FAQ = () => (
  <Styles>
    <Container>
      <h4 className="title">Sıkça Sorulan Sorular</h4>

      <div className="faq-content">
        <div className="faq-question">
          <input id="q1" type="checkbox" className="panel" />
          <div className="plus">+</div>
          <label for="q1" className="panel-title">Deniz Taksi Nedir?</label>
          <div className="panel-content">A woodchuck would chuck all the wood he could chuck, if a woodchuck
            could chuck wood!</div>
        </div>

        <div className="faq-question">
          <input id="q2" type="checkbox" className="panel" />
          <div className="plus">+</div>
          <label for="q2" className="panel-title">Deniz Taksi Güvenli midir?</label>
          <div className="panel-content">
            A woodchuck would chuck all the wood he could chuck, if a woodchuck
            could chuck wood!
          </div>
        </div>

        <div className="faq-question">
          <input id="q3" type="checkbox" className="panel" />
          <div className="plus">+</div>
          <label for="q3" className="panel-title">
          Hava Alanı Karşılama Himeti Nedir?
          </label>
          <div className="panel-content">
            Certain questions are better left &nbsp;{" "}
            <a
              href="https://en.wikipedia.org/wiki/The_Unanswered_Question"
              target="_blank"
            >
              unanswered
            </a>
          </div>
        </div>

        <div className="faq-question">
          <input id="q2" type="checkbox" className="panel" />
          <div className="plus">+</div>
          <label for="q2" className="panel-title">Deniz Taksi'de  Ödeme Nasıl Alınır?</label>
          <div className="panel-content">
            A woodchuck would chuck all the wood he could chuck, if a woodchuck
            could chuck wood!
          </div>
        </div>
        <div className="faq-question">
          <input id="q2" type="checkbox" className="panel" />
          <div className="plus">+</div>
          <label for="q2" className="panel-title">Deniz Taksi'de  Yeme İçme Koşulları Nelerdir?</label>
          <div className="panel-content">
            A woodchuck would chuck all the wood he could chuck, if a woodchuck
            could chuck wood!
          </div>
        </div>
      </div>
    </Container>
  </Styles>
);
