import React from "react";
import { Container } from "react-bootstrap";
import styled from "styled-components";
import DatePicker from "react-bootstrap-date-picker";

const Styles = styled.div`
    form {
      position: relative;
      top: -90px;
      border-radius: 20px;
      font-size: 14px;
    }

    form input[type=text] {
      height: 48px;
      width: 100%;
      padding: 0 12px;
      border: 1px solid #f1f2f6;
      border-radius: 8px;
      font-size: 16px;
      text-align: left;
      letter-spacing: .3px;
      background: #a1a9c3;
    }
`;

export const TravelForm = () => (
  <Styles>
    <Container>
      <form className="shadow p-3 mb-5 bg-white rounded">
        <div className="row">
          <div className="col-3">
            <input
              name="firstStation"
              placeholder="First Station"
              className="form-control"
            />
          </div>
          <div className="col-3">
            <input
              name="firstStation"
              placeholder="First Station"
              className="form-control"
            />
          </div>
          <div className="col-4">
            <DatePicker id="example-datepicker" value={this.state.value} onChange={this.handleChange} />
          </div>
          <div className="col-2">
            <button type="submit" class="btn btn-primary mb-2">Rezervasyon Yap</button>
          </div>
        </div>
      </form>
    </Container>
  </Styles>
);
