import React from "react";
import styled from "styled-components";
import { Container } from "react-bootstrap";
import { BiMailSend } from "react-icons/bi";
import { GiWideArrowDunk } from "react-icons/gi";
import { MdDateRange } from "react-icons/md";

const Styles = styled.div`
  .vertical-process {
    display: table;
    width: 100%;
  }

  .vertical-process .row {
    display: table-cell;
  }

  .box {
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.13);
    width: 100%;
    height: auto;
    background-color: #fff;
    margin: 20px;
    padding: 20px;
    border-radius: 8px;
  }

  .box.left {
    text-align: right;
  }

  .box.right {
    text-align: left;
  }

  .box.left .icon,
  .box.right .icon {
    color: #aed6dc;
    text-align: center;
    margin-bottom: 10px;
  }

  .box.left .icon svg,
  .box.right .icon svg {
    width: 40px;
    height: 40px;
  }

  .arrow svg {
    width: 80px;
    height: 80px;
    transform: rotateZ(90deg);
    margin-top: 35px;
    color: #132968;
  }

  .right.arrow svg {
    text-align: right;
    transform: rotateZ(270deg);
  }
`;
export const About = () => (
  <Styles>
    <div className="">
      <Container>
        <h4 className="title">Hakkımızda</h4>
        <div className="row">
          <div className="col-6 box left">
            <div className="icon">
              <BiMailSend />
            </div>
            <div className="description">
              'Rezervasyon Yap' seçeneğiyle sistemimiz üzerinden 7/24 veya
              rezervasyon@deniztaksi.com adresine mail göndererek rezarvasyon
              yaptırabilirsiniz.
            </div>
          </div>
          <div className="col-5 right">
            <div className="arrow">
              <GiWideArrowDunk />
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col-6 left">
            <div className="arrow">
              <GiWideArrowDunk />
            </div>
          </div>
          <div className="col-5 box right">
            <div className="icon">
              <MdDateRange />
            </div>
            <div className="description">
              Anlık ya da İleri tarihli Deniz Taksi talebinde bulunabilirsiniz.
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col-6 box left">
            <div className="icon">
              <BiMailSend />
            </div>
            <div className="description">
              Rezervasyon işleminin tamamlanabilmesi için istenen saat, tarih,
              kalkış iskelesi ve yolcu iletişim bilgilerini eksiksiz
              girdiğinizden emin olunç
            </div>
          </div>
          <div className="col-5 right">
            <GiWideArrowDunk />
          </div>
        </div>
        <div className="row">
          <div className="col-6 box left">
            <div className="icon">
              <BiMailSend />
            </div>
            <div className="description">
              Rezervasyon işleminin tamamlanmasının hemen ardından onay mesajı
              kullanıcılara mail yolu ile iletilmektedir.
            </div>
          </div>
          <div className="col-5 right">
            <GiWideArrowDunk />
          </div>
        </div>
        <div className="row">
          <div className="col-6 box left">
            <div className="icon">
              <BiMailSend />
            </div>
            <div className="description">Rezervasyon tamamlandı.</div>
          </div>
          <div className="col-5 right">
            <GiWideArrowDunk />
          </div>
        </div>
      </Container>
    </div>
  </Styles>
);
