import React from "react";
import styled from "styled-components";
import { Container } from "react-bootstrap";
import { BiMailSend } from "react-icons/bi";
import { GiWideArrowDunk } from "react-icons/gi";

const Styles = styled.div`
  .vertical-process {
    display: table; 
    width: 100%;
  }

  .vertical-process .row {
    display: table-cell; 
  }

  .box{
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.13);
    width: 100%;
    height: 100%;
    background-color: #fff;
    margin: 20px;
    padding: 20px;
    border-radius: 8px;
  }

  .box.left {
    text-align: right;
  }

  .box.left .icon {
    color: #aed6dc;
    text-align: center;
  }

  .box.left .icon svg {
    width: 40px;
    height: 40px;
  }

  .right svg {
    width: 80px;
    height: 80px;
    transform: rotateZ(90deg);
  }
`;
export const About = () => (
  <Styles>
    <div className="">
      <Container>
          <h4 className="title">Hakkımızda</h4>
          <div className="row">
            <div className="col-6 box left">
              <div className="icon">
                <BiMailSend/>
              </div>
              <div className="description">
              'Rezervasyon Yap' seçeneğiyle sistemimiz üzerinden 7/24 veya rezervasyon@deniztaksi.com adresine mail göndererek rezarvasyon yaptırabilirsiniz.
              </div>
            </div>
            <div className="col-5 right">
              <GiWideArrowDunk/>
            </div>
          </div>
      </Container>
    </div>
  </Styles>
);
