import React from "react";
import { Link } from "react-router-dom";
import { Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import styled from "styled-components";
import logo from '../assets/logo.png';
import { CgProfile } from "react-icons/cg";

const Styles = styled.div`
  .navbar {
    background-color: #fff;
  }

  a,
  .navbar-brand,
  .navbar-nav .nav-link {
    font-weight: bold;
    color: #4a536b;

    &:hover {
      color: #ff9a8d;
      text-decoration: none;
    }
  }

  img {
      height: 65px;
  }

  .navbar .icon {
    font-size: 30px;
  }
`;

export const NavigationBar = () => (
  <Styles>
    <Navbar class="shadow-sm p-3 mb-5 bg-white rounded" expand="lg">
      <Container>
        <Navbar.Brand href="/"><img src={logo} alt="Logo" /></Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ml-auto">
            <Nav.Item>
              <Nav.Link>
                <Link to="/">Anasayfa</Link>
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link>
                <Link to="/about">Hakkımızda</Link>
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link>
                <Link to="/">Duraklar</Link>
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link>
                <Link to="/contact">İletişim</Link>
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link>
                <CgProfile className="d-inline-block icon"/>

                <NavDropdown className="d-inline-block" id="basic-nav-dropdown">
                  {/* <NavDropdown.Toogle aria-controls="basic-navbar-nav"> <CgProfile /> </NavDropdown.Toggle> */}
                  <NavDropdown.Item.Toggle><CgProfile className="icon"/></NavDropdown.Item.Toggle>
                  <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                  <NavDropdown.Divider />
                  <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                </NavDropdown>
              </Nav.Link>
            </Nav.Item>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  </Styles>
);
