import React from "react";
import { Container } from "react-bootstrap";
import styled from "styled-components";
import { BsArrowLeftRight } from "react-icons/bs";

const Styles = styled.div`
  height: 45px;

  form {
    border-radius: 20px;
  }

  .btn-group .btn-secondary {
    background-color: #fff;
    border-color: #fff;
    color: #132968;
    font-weight: bold;
  }

  .col-1 {
    text-align: center;
  }

  .round-arrow button {
    border-radius: 50%;
    width: 40px;
    height: 40px;
    background-color: #f0f0f0;
  }
`;

export const TravelForm = () => (
  <Styles>
    <Container>
      <div className="row">
        <div className="col-5"></div>
        <form className="col-7 shadow p-3 mb-5 bg-white rounded">
          <div className="row">
            <div className="col-6">
              <div className="btn-group">
                <div className="row">
                  <div className="col-6 dropdown"></div>
                  <div className="col-6 dropdown"></div>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <input
                name="firstStation"
                placeholder="Ä°lk Durak"
                className="form-control"
              />
            </div>
            <div className="col-1 round-arrow">
              <button>
                <BsArrowLeftRight />
              </button>
            </div>
            <div className="col-12">
              <input
                name="firstStation"
                placeholder="Son Durak"
                className="form-control"
              />
            </div>
            <div className="col-12"></div>
            <div className="col-4">
              <button type="submit" class="btn btn-primary mb-2">
                Rezervasyon Yap
              </button>
            </div>
          </div>
        </form>
      </div>
    </Container>
  </Styles>
);
